import styled from 'styled-components';
import {CodeIcon, RepoForkedIcon, CommentIcon, StarFillIcon, FileIcon} from '@primer/octicons-react';
import {Marginer} from './marginer';
import ReactRoundedImage from "react-rounded-image";
import Box from '@material-ui/core/Box';
import Divider from '@material-ui/core/Divider';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import { makeStyles } from '@material-ui/core/styles';

const Card = ({ image, username, html_url, totalFiles, createdAt, lastUpdated , forks_url,comments_url,starred_url,description,filename,file_url}) => {
  const classes = useStyles();

  return (
    <List className={classes.root}>
      <ListItem>
      <Box paddingLeft = {2} paddingRight = {2} paddingTop ={2} paddingBottom={2} alignItems = "center" justifyContent="center">
      <Heading>
      <Avatar>
      <ReactRoundedImage
          image={image}
          roundedColor="#321124"
          imageWidth="50"
          imageHeight="50"
          roundedSize="2"
          borderRadius="60"/>
      </Avatar>
        <Marginer direction = "horizontal" margin = {5} />
        <Username><a href={html_url}>{username}</a></Username>
        <Marginer direction = "horizontal" margin = {90} />


        <Files><a href={file_url}><icon><CodeIcon backgroundColor = "black" size = {24}/>
        </icon> {totalFiles} <Marginer direction = "horizontal" margin = {3} /> File</a> </Files>
        <Marginer direction = "horizontal" margin = {9} />

        <Forks>
          <a href={forks_url}>
            <icon><RepoForkedIcon size = {24}/></icon>
        <Marginer direction = "horizontal" margin = {3} />
          Forks</a>
        </Forks>
        <Marginer direction = "horizontal" margin = {9} />

        <Comments><a href={comments_url}> <icon><CommentIcon size = {24}/></icon>
        <Marginer direction = "horizontal" margin = {3} /> Comments</a></Comments>
        <Marginer direction = "horizontal" margin = {9} />

        <Stars><a href={starred_url}><icon><StarFillIcon size = {24}/></icon>
        <Marginer direction = "horizontal" margin = {3} /> Stars</a></Stars>
      </Heading>
      <Date>
        Created at: {createdAt} 
        <Marginer direction = "horizontal" margin = {50} />
        Last updated: {lastUpdated}
      </Date>
      <Info>
        {description}
      </Info>
      <Data><icon><FileIcon size = {24}/></icon>
      <Marginer direction = "horizontal" margin = {3} />
      <a href={file_url}>{filename}</a></Data>
      <Marginer direction = "vertical" margin = {5} />
      <Divider light />
      </Box>
      </ListItem>
      </List>


  );
};

const useStyles = makeStyles((theme) => ({
  root: {
    width: '100%',
    backgroundColor: theme.palette.background.paper,
  },
}));


const Heading = styled.div`
  background: white;
  display: flex;
  flex-direction: space-between;
  align-items: center;
`;

const Avatar = styled.div`
    background: white;
    display: flex;
    flex-direction: row;
    justify-content: flex-start;
    align-items: left;
`;

const Date = styled.div`
  background: white;
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content:flex-start;
  height: auto;
  width: auto;
`;


const Info = styled.div`
  background: white;
  text-color: gray
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  height: auto;
  width: auto;

`;

const Username = styled.div`
  background: White;
`;

const Files = styled.div`
  background: white;
`;

const Forks = styled.div`
  background: White;


`;

const Comments = styled.div`
  background: White;

`;

const Stars = styled.div`
  background: White;

`;

const Data = styled.div`
  background: White;
  display:flex;
  flex-direction: row;
  height: auto;
  width: auto;
`;

export default Card;
