import { useState, useEffect } from 'react';
import styled from 'styled-components';

import GlobalStyles from './GlobalStyle';

import Header from './components/Header';
import GistList from './components/GistList';

import { getPublicGists } from './services/gistService';

const App = () => {
  const [gists, setGists] = useState([]);
  const [username, setUsername] = useState('');
  const usernameCallBack = username => setUsername(username);

  // only runs one time when component is mounted
  useEffect(
    () =>
      getPublicGists()
        .then(res => res.data)
        .then(gists => setGists(gists)),
    []

  );

  useEffect(() => {
    if (username) {
      const filteredGists = gists.filter(gist => gist.owner.login === username);
      setGists(filteredGists);
      //console.log(gists.filter(gist => gist.owner.login === username));
    }
  }, [username]);



  return (
    <Wrapper className="App" data-testid="app">
      <Header usernameCallBack={usernameCallBack} />
      <GistList gists={gists} />
      <GlobalStyles />
    </Wrapper>
  );
};

const Wrapper = styled.div`
  font-size: 14px;
  line-height: 1.5;
`;

export default App;
